(381, 'Tham mưu KH đầu tư công giai đoạn 2021-2015', '<div style=\"font-family: monospace; font-size: 24px;\">- Ph&ograve;ng HCTCTV tham mưu đề nghị x&acirc;y dựng cơ sở vật chất, lưu &yacute; hai PKĐK.</div>\r\n<div style=\"font-family: monospace; font-size: 24px;\">- Ph&ograve;ng KHNV tham mưu về trang thiết bị chuy&ecirc;n m&ocirc;n, Lưu &yacute; m&aacute;y X quang, si&ecirc;u &acirc;m... cho hai PKĐK.</div>', 'tasks', 1, '2019-04-04', '2019-04-15', 0, 1, '', 0, 0, 0, 1554347264),
(382, 'a', '<p>a</p>', 'tasks', 1, '2019-04-05', '2019-04-08', 0, 0, '', 0, 0, 0, 1554428703),
(383, 'Bản chất kế hoạch cá nhân', '<p>Bản chất kế hoạch c&aacute; nh&acirc;n giống như cửa sổ \"Việc của bạn\" nếu c&aacute; nh&acirc;n tự giao việc cho ch&iacute;nh m&igrave;nh. Khi mở cửa sổ \"việc của bạn\" th&igrave; hiện n&ecirc;n tất cả c&ocirc;ng việc được giao v&agrave; tự giao, c&oacute; thời hạn v&agrave; đ&aacute;nh gi&aacute; rồi. Nếu c&aacute;nh nh&acirc;n tự giao việc được cho m&igrave;nh th&igrave; cửa sổ \"<strong>Việc của bạn</strong>\" ch&iacute;nh l&agrave; cửa sổ \"<strong>Kế hoạch c&aacute; nh&acirc;n</strong>\".&nbsp;</p>\r\n<p>Vấn đề l&agrave; h&agrave;ng tuần, h&agrave;ng th&aacute;ng, qu&yacute;, năm, Người quản l&yacute; muốn xuất ra file dưới dạng Excel để xem ai được giao nhiều hay &iacute;t việc nhất, l&agrave;m tốt nhất, đ&uacute;ng hạn.... để đ&aacute;nh gi&aacute;.</p>\r\n<p>&nbsp;</p>', 'tasks', 1, '2019-04-05', '2019-04-08', 0, 1, '', 0, 0, 0, 1554429267),
(384, 'Đăng tài liệu công việc.', '<p>Ch&uacute; đăng t&agrave;i liệu c&ocirc;ng việc anh đ&atilde; gửi l&ecirc;n cổng xem. Ch&uacute; x&acirc;y dựng cho ph&ugrave; hợp với <strong>định dạng ấy</strong>. Đ&acirc;y chỉ l&agrave; một c&ocirc;ng việc của TYT x&atilde;. C&ograve;n 33 c&ocirc;ng việc tương tự nhưng khối lượng c&ocirc;ng việc kh&aacute;c c&ograve;n lớn hơn thế. C&aacute;c khoa, ph&ograve;ng cũng c&oacute; c&aacute;c c&ocirc;ng việc tương tự.&nbsp;</p>', 'tasks', 1, '2019-04-05', '2019-04-10', 0, 1, '', 0, 0, 0, 1554429550),
(385, 'Hồ sơ khám chữa bệnh BHYT', '<p>CN Quyết khẩn trương phối hợp 5 trạm Y tế: Thanh L&acirc;m, Tiến Thắng, Li&ecirc;n Mạc, Thạch Đ&agrave;, Đại Thịnh ho&agrave;n thiện hồ sơ bao gồm: Phụ lục 1, 2, 3, bi&ecirc;n bản thẩm định, c&ocirc;ng văn đề nghị theo hướng dẫn tại c&ocirc;ng văn số 95/BHXH. Ho&agrave;n tất thủ tục v&agrave; gửi hồ sơ về BHXH Th&agrave;nh phố trước ng&agrave;y 12/4.</p>', 'tasks', 107, '2019-04-08', '2019-04-12', 0, 1, '<p>Th?ng nh?t v?i BHXH Huy?n v&agrave; th&ocirc;ng b&aacute;o cho c&aacute;c x&atilde; l?ch th?m ??nh.</p>', 0, 0, 0, 1554705564),
(386, 'Xây dựng kế hoạch nghiên cứu khoa học của đơn vị', '<p>X&acirc;y dựng Kế hoạch nghi&ecirc;n cứu khoa học của đơn vị năm 2019</p>', 'tasks', 107, '2019-04-08', '2019-04-09', 0, 1, '<p>Xong tr??c ng&agrave;y 09/4/2019</p>', 0, 0, 0, 1554705704),
(387, 'Tổng hợp và báo cáo các ý kiến góp ý về phần mềm VNT - HIS', '<p>Em tổng hợp c&aacute;c &yacute; kiến phản hồi của 18 trạm Y tế x&atilde;, thị trấn v&agrave; 02 ph&ograve;ng kh&aacute;m đa khoa về hoạt động của phần mềm VNPTHIS: n&ecirc;u được kh&oacute; khăn, vướng mắc, thuận lợi khi thực hiện phần mềm n&agrave;y trong c&ocirc;ng t&aacute;c thanh quyết to&aacute;n KCB BHYT</p>', 'tasks', 107, '2019-04-08', '2019-04-08', 0, 1, '<p>L?p b?ng b&aacute;o c&aacute;o</p>', 0, 0, 0, 1554705997),
(388, 'Tổng hợp báo cáo thống kê Quý I/2019', '<p>Ho&agrave;n thiện b&aacute;o c&aacute;o thống k&ecirc; Qu&yacute; I/2019 xong trước ng&agrave;y 12/4/2019</p>', 'tasks', 107, '2019-04-08', '2019-04-12', 0, 0, '<p>Ho&agrave;n thi?n v&agrave; g?i v? S? tr??c ng&agrave;y 15/4/2019</p>', 0, 0, 0, 1554706366),
